import { THEME } from '@common/constants/theme.constant';
import { Menu, SubMenuProps } from 'antd';
import React from 'react';
import { css } from 'twin.macro';

const { SubMenu: AntdSubMenu } = Menu;

export * from './menu-item.component';

interface IProps extends SubMenuProps {
    className?: string;
    name: string;
}

export const SubMenu = (props: IProps): JSX.Element => {
    return <AntdSubMenu css={styles.root} {...props} />;
};

const styles = {
    root: css`
        && .ant-table-thead {
            background-color: ${THEME.GREEN};
            color: white;
        }

        && .ant-menu-sub {
            background-color: transparent;
        }
        && .ant-menu-submenu-title {
            font-weight: normal;
            margin: 0;
        }
        &&. ant-menu-submenu {
            border-right: 0;
            background-color: white;
        }
        && .ant-menu-title-content {
            font-weight: normal;
            padding-top: 0 !important;
            padding-bottom: 0 !important;
            margin: 0;
            font-size: ${THEME.BASE_FONT_SIZE};
        }
    `,
};
