import { useField } from '@common/hooks/use-field.hook';
import React, { useCallback } from 'react';
import { DatePicker, IDatePickerChangeHandler, IDatePickerProps } from './date-picker.component';

export function FormDatePicker<TFormValues>(
    props: Omit<IDatePickerProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, onBlur, getCustomErrorMessage } = props;
    const { setFieldValue, setFieldTouched, value, errorMessage, triggerValidate } = useField<Date | undefined>({
        ...props,
        getCustomErrorMessage,
    });

    const handleChange = useCallback<IDatePickerChangeHandler>(
        (e) => {
            onChange && onChange(e);
            setFieldValue(e.value);

            triggerValidate();
        },
        [setFieldValue, onChange, triggerValidate],
    );

    const handleBlur = useCallback(
        (e: React.FocusEvent<HTMLInputElement>) => {
            onBlur && onBlur(e);
            setFieldTouched();
        },
        [onBlur, setFieldTouched],
    );

    return (
        <DatePicker
            errorMessage={errorMessage}
            {...props}
            onChange={handleChange}
            onBlur={handleBlur}
            name={props.name as string}
            value={value}
        />
    );
}
