// import 'twin.macro';
import { Loading } from '@common/general-ui/loading/loading.component';
import { Pagination } from '@common/general-ui/pagination/pagination.component';
import { ITableColumn } from '@common/general-ui/table/table.component';
import { useIsScrollEnd } from '@common/hooks/use-is-scroll-end.hook';
import { IFetchMoreRequestOptions } from '@common/interfaces/collection-request-options.interface';
import { ICollection } from '@common/interfaces/collection.interface';
import { IIdObject } from '@common/interfaces/id-object.interface';
import React, { useCallback, useMemo } from 'react';
import { css } from 'twin.macro';
import { MobileTableRow } from './mobile-table-row.component';

export interface IRenderRowProps<T extends IIdObject, TKey> {
    rowData: T;
    columns: ITableColumn<T, TKey>[];
}

interface IProps<T extends IIdObject, TKey> {
    name: string;
    columns: ITableColumn<T, TKey>[];
    dataSource: ICollection<T> | undefined;
    className?: string;
    renderRow?: (props: IRenderRowProps<T, TKey>) => JSX.Element;
    isLoading: boolean;
    isFullWidth?: boolean;
    noHeader?: boolean;
    onFetchMore?: (pagination: IFetchMoreRequestOptions) => void;
}

export function MobileTable<T extends IIdObject, TKey>({
    dataSource,
    columns,
    className,
    renderRow,
    onFetchMore,
    isLoading,
    isFullWidth,
    noHeader,
    ...rest
}: IProps<T, TKey>): JSX.Element {
    const visibleColumns = useMemo(() => columns.filter((f) => f.isVisible), [columns]);
    const [first, ...restVisibleColumns] = visibleColumns;

    const next_page = dataSource?.next_page;
    useIsScrollEnd({
        next_page,
        onScrollEnd: useCallback(() => {
            onFetchMore && onFetchMore({ next_page: dataSource?.next_page });
        }, [dataSource?.next_page, onFetchMore]),
    });

    return (
        <div className={className} data-testid={rest.name}>
            <Loading isLoading={isLoading}>
                <div
                    tw="grid-cols-1 lg:grid-cols-2 grid xl:grid-cols-3 grid-column-gap[16px] grid-row-gap[16px]"
                    css={isFullWidth && styles.fullWidth}
                >
                    {visibleColumns.length &&
                        (dataSource?.list || []).map((item) => {
                            return renderRow ? (
                                <React.Fragment key={item.id}>
                                    {renderRow({
                                        rowData: item,
                                        columns: restVisibleColumns,
                                    })}
                                </React.Fragment>
                            ) : (
                                <MobileTableRow
                                    header={first}
                                    key={item.id}
                                    columns={restVisibleColumns}
                                    rowData={item}
                                    isFullWidth={isFullWidth}
                                    noHeader={noHeader}
                                />
                            );
                        })}
                </div>

                {dataSource && <Pagination tw="pt-4 text-right" dataSource={dataSource} onFetchMore={onFetchMore} />}

                {isLoading && !dataSource?.list && <div tw="py-60"></div>}
            </Loading>
        </div>
    );
}

const styles = {
    fullWidth: css`
        grid-template-columns: auto !important;
    `,
};
