import { MQ } from '@common/constants/mq.constant';
import { THEME } from '@common/constants/theme.constant';
import { Modal as AntModal } from 'antd';
import React, { ReactNode } from 'react';
import { css } from 'twin.macro';

interface IProps {
    name: string;
    className?: string;
    children: ReactNode;
    title: ReactNode;
    renderFooter?: () => ReactNode;
    onCloseClick?: () => void;
    closable?: boolean;
    centered?: boolean;
    closeIcon?: ReactNode;
}

export type IModalReturn = [() => void, () => void];

export const Modal = ({
    name,
    className,
    children,
    title,
    onCloseClick,
    renderFooter,
    closable = true,
    centered = false,
    closeIcon,
}: IProps): JSX.Element => {
    return (
        <AntModal
            centered={centered}
            modalRender={(node): JSX.Element => <div data-testid={`modal-${name}`}>{node}</div>}
            width={''}
            className={className}
            onCancel={onCloseClick}
            css={styles.root}
            title={title}
            visible={true}
            closable={closable}
            footer={renderFooter ? <>{renderFooter()}</> : null}
            closeIcon={closeIcon}
        >
            {children}
        </AntModal>
    );
};

const styles = {
    root: css`
        top: 0;

        .ant-modal-content {
            margin: 8px;
            margin-top: 2px;

            ${MQ.lg} {
                margin-top: 40px;
            }
        }
        .ant-modal-header {
            background-color: ${THEME.GREEN};
            color: white;
        }
        .ant-modal-body {
            padding: 16px;
        }
        .ant-modal-title {
            color: white;
            font-size: ${THEME.BASE_FONT_SIZE};
            font-weight: 500;
        }
        .ant-modal-close-x {
            margin-top: -2px;
            color: white;
        }
    `,
};
