import { useField } from '@common/hooks/use-field.hook';
import { RcFile } from 'antd/lib/upload';
import React, { useCallback } from 'react';
import { IUploadImageChangeHandler, IUploadImageProps, UploadImage } from './upload-image.component';

export function FormUploadImage<TFormValues>(
    props: Omit<IUploadImageProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, onBlur, getCustomErrorMessage } = props;
    const { setFieldValue, setFieldTouched, value, errorMessage } = useField<RcFile>({
        ...props,
        getCustomErrorMessage,
    });

    const handleChange = useCallback<IUploadImageChangeHandler>(
        (e) => {
            onChange && onChange(e);
            setFieldValue(e.target.value as RcFile);
        },
        [setFieldValue, onChange],
    );

    const handleBlur = useCallback(
        (e: React.FocusEvent<unknown>) => {
            onBlur && onBlur(e);
            setFieldTouched();
        },
        [onBlur, setFieldTouched],
    );

    return (
        <>
            <UploadImage
                errorMessage={errorMessage}
                {...props}
                onChange={handleChange}
                onBlur={handleBlur}
                name={props.name as string}
                value={value}
            />
        </>
    );
}
