import { THEME } from '@common/constants/theme.constant';
import React, { ReactNode } from 'react';
import { css } from 'twin.macro';

interface IProps {
    children: ReactNode;
    className?: string;
}

export const DropDownPopup = ({ children, className }: IProps): JSX.Element => {
    return (
        <div className={className} css={styles.popup} data-testid="customizeTablePopup">
            {children}
        </div>
    );
};

const styles = {
    popup: css`
        background-color: white;
        min-width: 300px;
        padding: 8px;
        border-radius: 4px;
        border: 1px solid ${THEME.GRAY};
    `,
};
