import clsx from 'clsx';
import React, { InputHTMLAttributes } from 'react';
import './DefaultInput.css';

const DefaultInput = (
  props: InputHTMLAttributes<HTMLInputElement>
): JSX.Element => {
  const { className, ...rest } = props;

  return (
    <input
      className={clsx('react-loqate-input', className)}
      data-testid="default-input"
      {...rest}
    />
  );
};

export default DefaultInput;
import clsx from 'clsx';
import React, { forwardRef, OlHTMLAttributes, Ref } from 'react';
import './DefaultList.css';

const DefaultList = forwardRef(
  (
    props: OlHTMLAttributes<HTMLUListElement>,
    ref: Ref<HTMLUListElement>
  ): JSX.Element => {
    const { className, ...rest } = props;

    return (
      <ul
        className={clsx('react-loqate-default-list', className)}
        data-testid="default-list"
        {...rest}
        ref={ref}
      />
    );
  }
);

export default DefaultList;
import clsx from 'clsx';
import React, { LiHTMLAttributes } from 'react';
import { Item } from '..';
import './DefaultListItem.css';

const DefaultListItem = (
  props: LiHTMLAttributes<HTMLLIElement> & { suggestion: Item }
): JSX.Element => {
  const { className, value, children, suggestion, ...rest } = props;

  const regex = new RegExp(
    `(.*)(${value?.toString().replace(/\W/g, '')})(.*)`,
    'i'
  );

  const search = `${suggestion.Text} ${suggestion.Description}`;

  const result = search.match(regex);
  const [before, match, after] = result?.slice(1) ?? ['', '', ''];

  return (
    <li
      className={clsx('react-loqate-list-item', className)}
      aria-label={match}
      tabIndex={0}
      {...rest}
    >
      {result?.length === 4 ? (
        <>
          {before}
          <strong>{match}</strong>
          {after}
        </>
      ) : (
        search
      )}
    </li>
  );
};

export default DefaultListItem;
export const LOQATE_BASE_URL = 'https://api.addressy.com/Capture/Interactive';
export const LOQATE_RETRIEVE_URL = 'Retrieve/v1/json3.ws';
export const LOQATE_FIND_URL = 'Find/v1.10/json3.ws';
import axios, { AxiosInstance } from 'axios';
import qs from 'qs';
import {
  LOQATE_BASE_URL,
  LOQATE_FIND_URL,
  LOQATE_RETRIEVE_URL,
} from '../constants/loqate';

interface FindQuery {
  text: string;
  language: string;
  countries?: string[];
  limit?: number;
  containerId?: string;
}

class Loqate {
  public _httpClient: AxiosInstance;

  constructor(public key: string, public baseUrl: string = LOQATE_BASE_URL) {
    this._httpClient = axios.create({
      baseURL: baseUrl,
      params: {
        Key: key,
      },
      paramsSerializer: (params) =>
        qs.stringify(params, {
          arrayFormat: 'comma',
        }),
    });
  }

  public static create(key: string, baseUrl: string = LOQATE_BASE_URL) {
    return new Loqate(key, baseUrl);
  }

  public retrieve(id: string) {
    return this._httpClient.get(LOQATE_RETRIEVE_URL, {
      params: {
        Id: id,
      },
    });
  }

  public async find(query: FindQuery) {
    const { text, countries = [], containerId, language, limit } = query;

    const response = await this._httpClient.get(LOQATE_FIND_URL, {
      params: {
        Text: text,
        Countries: countries,
        Container: containerId,
        limit,
        language,
      },
    });

    const error = response?.data?.Items.find((e: any) => e.Error);
    if (error) {
      throw new Error(`Loqate error: ${JSON.stringify(error)}`);
    }

    return response;
  }
}

export default Loqate;
import { useCallback, useEffect } from 'react';

function useDebounceEffect(
  effect: () => void,
  delay: number | undefined,
  deps: any[]
) {
  const callback = useCallback(effect, deps);

  useEffect(() => {
    if (!delay) {
      callback();
      return;
    }

    const handler = setTimeout(() => {
      callback();
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [callback, delay]);
}

export default useDebounceEffect;
import { ClickAwayListener, Portal } from '@mui/base';
import clsx from 'clsx';
import React, {
  ChangeEvent,
  ComponentType,
  useMemo,
  useRef,
  useState,
} from 'react';
import DefaultInput from './components/DefaultInput';
import DefaultList from './components/DefaultList';
import DefaultListItem from './components/DefaultListItem';
import Loqate from './utils/Loqate';
import useDebounceEffect from './utils/useDebounceEffect';

export interface Props {
  locale: string;
  apiKey: string;
  countries?: string[];
  limit?: number;
  onSelect: (address: Address) => void;
  components?: Components;
  className?: string;
  classes?: {
    input?: string;
    list?: string;
    listItem?: string;
  };
  inline?: boolean;
  debounce?: number;
}

interface Components {
  Input?: ComponentType;
  List?: ComponentType;
  ListItem?: ComponentType<{ suggestion: Item }>;
}

export interface Address {
  AdminAreaCode: string;
  AdminAreaName: string;
  Barcode: string;
  Block: string;
  BuildingName: string;
  BuildingNumber: string;
  City: string;
  Company: string;
  CountryIso2: string;
  CountryIso3: string;
  CountryIsoNumber: number;
  CountryName: string;
  DataLevel: string;
  Department: string;
  District: string;
  DomesticId: string;
  Field1: string;
  Field2: string;
  Field3: string;
  Field4: string;
  Field5: string;
  Field6: string;
  Field7: string;
  Field8: string;
  Field9: string;
  Field10: string;
  Field11: string;
  Field12: string;
  Field13: string;
  Field14: string;
  Field15: string;
  Field16: string;
  Field17: string;
  Field18: string;
  Field19: string;
  Field20: string;
  Id: string;
  Label: string;
  Language: string;
  LanguageAlternatives: string;
  Line1: string;
  Line2: string;
  Line3: string;
  Line4: string;
  Line5: string;
  Neighbourhood: string;
  POBoxNumber: string;
  PostalCode: string;
  Province: string;
  ProvinceCode: string;
  ProvinceName: string;
  SecondaryStreet: string;
  SortingNumber1: string;
  SortingNumber2: string;
  Street: string;
  SubBuilding: string;
  Type: string;
}

const loqateLanguage = (language: string): string => {
  const [languageCode] = language.replace('_', '-').split('-');
  return languageCode;
};

export interface Item {
  Id: string;
  Description: string;
  Type: 'Address' | 'Postcode' | 'Residential' | 'Street';
  Text: string;
  Highlight: string;
}

function AddressSearch(props: Props) {
  const {
    locale,
    countries,
    onSelect,
    limit,
    apiKey,
    className,
    classes,
    components,
    inline,
    debounce,
  } = props;
  const loqate = useMemo(() => Loqate.create(apiKey), [apiKey]);

  const [suggestions, setSuggestions] = useState<any[]>([]);
  const [value, setValue] = useState('');

  const anchorRef = useRef<HTMLDivElement>(null);
  const rect = anchorRef.current?.getBoundingClientRect();

  async function selectSuggestion({ Type, Id }: Item) {
    if (Type === 'Address') {
      const { data } = await loqate.retrieve(Id);
      const { Items = [] } = data;

      if (Items.length) {
        setSuggestions([]);
      }

      onSelect(Items[0]);
      return;
    }

    const items = await find(value, Id);
    setSuggestions(items);
  }

  async function find(text: string, containerId?: string) {
    const { data } = await loqate.find({
      countries,
      limit,
      text,
      containerId,
      language: loqateLanguage(locale),
    });

    if (data?.Items) {
      return data.Items;
    }

    return [];
  }

  async function handleChange({ target }: ChangeEvent<HTMLInputElement>) {
    const { value: search } = target;

    setValue(search);
  }

  useDebounceEffect(
    () => {
      if (value === '') {
        setSuggestions([]);
        return;
      }

      find(value).then(setSuggestions);
    },
    debounce,
    [value]
  );

  const Input = components?.Input ?? DefaultInput;
  const List = components?.List ?? DefaultList;
  const ListItem = components?.ListItem ?? DefaultListItem;

  return (
    <div ref={anchorRef} className={className} data-testid="react-loqate">
      <Input
        className={clsx(classes?.input)}
        onChange={handleChange}
        value={value}
        data-testid="react-loqate-input"
      />

      <Portal container={document.body} disablePortal={inline}>
        <ClickAwayListener onClickAway={() => setSuggestions([])}>
          <List
            style={{
              position: 'absolute',
              top: rect ? (rect.y ?? 0) + rect.height + window.scrollY : 0,
              left: rect?.left ?? 0,
              width: rect?.width ?? undefined,
            }}
            hidden={!suggestions.length}
            className={classes?.list}
            data-testid="react-loqate-list"
          >
            {suggestions.map((suggestion, i) => (
              <ListItem
                key={suggestion.Id + i}
                onClick={() => selectSuggestion(suggestion)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    selectSuggestion(suggestion);
                  }
                }}
                className={classes?.listItem}
                data-testid={`react-loqate-list-item-${suggestion.Id}`}
                value={value}
                suggestion={suggestion}
              >
                {suggestion.Text} {suggestion.Description}
              </ListItem>
            ))}
          </List>
        </ClickAwayListener>
      </Portal>
    </div>
  );
}

export default AddressSearch;
