import { CONST } from '@common/constants/const.constant';
import { ICollectionRequestOptions } from '@common/interfaces/collection-request-options.interface';
import { useCallback, useRef } from 'react';
import { QueryClient } from 'react-query';

interface IReturn<T = unknown> {
    getCollectionOptions: () => ICollectionRequestOptions<T>;
    setCollectionOptions: (pagination: ICollectionRequestOptions<T>) => void;
}

interface IProps<TFilter> {
    queryStorage?: { queryClient: QueryClient; storageKey: string };
    limit?: number;

    filter?: TFilter;
}
export function useCollectionOptions<TFilter = unknown>({
    limit = CONST.LIST_LIMIT,
    queryStorage,
    filter = {} as TFilter,
}: IProps<TFilter>): IReturn<TFilter> {
    const ref = useRef<ICollectionRequestOptions<TFilter>>(
        queryStorage
            ? queryStorage.queryClient.getQueryData(`settings-${queryStorage.storageKey}`) || { limit, filter }
            : { limit, filter },
    );

    return {
        getCollectionOptions: useCallback((): ICollectionRequestOptions<TFilter> => ref.current, []),
        setCollectionOptions: useCallback(
            (data: ICollectionRequestOptions<TFilter>): void => {
                ref.current = data;
                if (queryStorage?.queryClient && queryStorage.storageKey) {
                    queryStorage.queryClient?.setQueryData(`settings-${queryStorage.storageKey}`, () => data);
                }
            },
            // eslint-disable-next-line react-hooks/exhaustive-deps
            [queryStorage?.storageKey],
        ),
    };
}
