import { CognitoHostedUIIdentityProvider } from '@aws-amplify/auth/lib/types';
import { CONST } from '../constants/const.constant.js';
import { EAuthentication } from '../enums/authentication.enum.js';
import { sessionStorageService } from '../services/session-storage.service.js';
import { Amplify, Auth } from 'aws-amplify';
import { useCallback } from 'react';
var AmplifyEx = Amplify;
var useCognito = function useCognito() {
  return {
    loginWithFacebook: useCallback(function (info) {
      sessionStorageService.save(CONST.sessionStorageKey_SSO, info);
      Auth.federatedSignIn({
        provider: CognitoHostedUIIdentityProvider.Facebook
      });
    }, []),
    loginWithGoogle: useCallback(function (info) {
      sessionStorageService.save(CONST.sessionStorageKey_SSO, info);
      Auth.federatedSignIn({
        provider: CognitoHostedUIIdentityProvider.Google
      });
    }, []),
    signOut: useCallback(function () {
      sessionStorageService.save(CONST.sessionStorageKey_SSO, EAuthentication.Logout);
      Auth.signOut();
    }, []),
    signOutRegistration: useCallback(function () {
      sessionStorageService.save(CONST.sessionStorageKey_SSO, EAuthentication.RegistrationSignOut);
      Auth.signOut();
    }, []),
    currentAuthenticatedUser: useCallback(function () {
      return Auth.currentAuthenticatedUser();
    }, [])
  };
};
export { AmplifyEx, useCognito };
