import { THEME } from '@common/constants/theme.constant';
import { Field, IFieldProps } from '@common/general-ui/field/field.component';
import { Readonly } from '@common/general-ui/readonly.component';
import { SerializedStyles } from '@emotion/react';
import React, { useCallback, useEffect, useState } from 'react';
import { css } from 'twin.macro';

export type ITextAreaChangeHandler = (e: React.ChangeEvent<HTMLTextAreaElement>) => void;

export interface ITextAreaProps<TName = string> extends IFieldProps<string, TName> {
    maxLength?: number;
    className?: string;
    onChange?: ITextAreaChangeHandler;
    onBlur?: (e: React.FocusEvent<HTMLTextAreaElement>) => void;
}

// eslint-disable-next-line react/display-name
export const TextArea: React.FC<ITextAreaProps> = (props): JSX.Element => {
    const { placeholder, onBlur, name, maxLength } = props;

    const [val, setVal] = useState(props.defaultValue || props.value);
    useEffect(() => {
        if (props.value !== undefined) {
            setVal(props.value);
        }
    }, [props.value]);

    useEffect(() => {
        if (props.defaultValue !== undefined) {
            setVal(props.defaultValue);
        }
    }, [props.defaultValue]);

    const handleChange = useCallback(
        (e: React.ChangeEvent<HTMLTextAreaElement>) => {
            const newValue = e.target.value;

            setVal(newValue);
            props.onChange && props.onChange(e);
        },
        [props],
    );
    return (
        <Field {...props}>
            <div tw="relative">
                {props.readOnly && <Readonly />}
                <textarea
                    tabIndex={props.readOnly ? -1 : 0}
                    readOnly={props.readOnly}
                    name={name}
                    rows={5}
                    css={styles.textArea(!!props.readOnly)}
                    placeholder={placeholder}
                    onChange={handleChange}
                    onBlur={onBlur}
                    maxLength={maxLength}
                    value={val}
                ></textarea>
            </div>
        </Field>
    );
};

const styles = {
    textArea: (readOnly: boolean): (SerializedStyles | boolean)[] => [
        css`
            padding: 6px 12px;
            height: auto;
            border: 1px solid ${THEME.GRAY};
            border-radius: 4px;
            font-weight: 400;
            color: ${THEME.BLACK};
            display: block;
            width: 100%;
            font-size: ${THEME.BASE_FONT_SIZE};
            box-sizing: border-box;

            :focus {
                outline-color: transparent;
                outline-style: none;
                ${THEME.BOX_SHADOW_GREEN}
            }
            ::placeholder {
                color: ${THEME.GRAY};
                font-weight: thin;
            }
        `,
        readOnly &&
            css`
                border: 1px solid ${THEME.READONLY_COLOR};
                :focus {
                    outline-color: transparent;
                    outline-style: none;
                    box-shadow: none;
                }
                &&.ant-input-affix-wrapper:focus-within {
                    outline-color: transparent;
                    outline-style: none;
                    box-shadow: none;
                }
            `,
    ],
};
