import { SerializedStyles } from '@emotion/react';
import React from 'react';
import { css } from 'twin.macro';

interface IProps {
    firstName: string;
    lastName: string;
}
export const Avatar = ({ firstName, lastName }: IProps): JSX.Element => {
    const f = firstName.length ? firstName[0] : '';
    const l = lastName.length ? lastName[0] : '';
    const key = `${f}${l}`;
    return (
        <div css={styles.root(getColor(key))}>
            {f}
            {l}
        </div>
    );
};

const COLORS = [
    '#EA6A47',
    '#1c4e80',
    '#ac3e31',
    '#6ab187',
    '#488a99',
    '#00915d',
    '#d32d41',
    '#f2b63e',
    'rgb(0, 171, 82)',
    'rgb(0, 107, 255)',
    'rgb(255, 0, 133)',
];

const TMP_CACHE: Record<string, string> = {};
const getColor = (key: string): string => {
    if (TMP_CACHE[key]) {
        return TMP_CACHE[key];
    }

    const index = Math.floor(Math.random() * COLORS.length);
    TMP_CACHE[key] = COLORS[index];
    return COLORS[index];
};
const styles = {
    root: (color: string): SerializedStyles => css`
        text-transform: uppercase;
        border-radius: 50%;
        background-color: ${color};
        width: 34px;
        height: 34px;
        padding-top: 4px;
        display: flex;
        justify-content: center;
        align-items: center;
        color: white;
    `,
};
