/* eslint-disable @typescript-eslint/no-explicit-any */
import { CloseCircleOutlined } from '@ant-design/icons';
import { THEME } from '@common/constants/theme.constant';
import { Field, IFieldProps } from '@common/general-ui/field/field.component';
import { Input } from '@common/general-ui/input/input.component';
import { consoleService } from '@common/services/console.service';
import { generateId } from '@common/utils/generate-id.util';
import { Global } from '@emotion/react';
import React, { useCallback, useEffect, useMemo, useState } from 'react';
import ReactTestUtils from 'react-dom/test-utils';
import AddressSearch from 'react-loqate';
import { css } from 'twin.macro';

export interface IInputLoqateValue {
    addressLine1: string;
    addressLine2: string;
    addressLine3: string;
    city: string;
    postCode: string;
    originalLabel: string;
    label: string;
    country: string;
}
const EMPTY: IInputLoqateValue = {
    addressLine1: '',
    addressLine2: '',
    addressLine3: '',
    country: '',
    city: '',
    postCode: '',
    originalLabel: '',
    label: '',
};
export type IInputLoqateChangeHandler = (arg: { name: string; value: IInputLoqateValue }) => void;
export interface IInputLoqateProps<TName = string> extends IFieldProps<IInputLoqateValue, TName> {
    className?: string;
    onChange?: IInputLoqateChangeHandler;
    onBlur?: (e: React.FocusEvent) => void;
}

export const InputLoqate = (props: IInputLoqateProps): JSX.Element => {
    const html_id = useMemo(() => generateId('html_id'), []);
    const { onChange, name, placeholder, value, errorMessage } = props;
    const [address, setAddress] = useState(value);

    useEffect(() => {
        setAddress(value);
    }, [value]);

    const handleClear = useCallback((): void => {
        setAddress(EMPTY);

        const loqateInput = document.getElementById(html_id) as HTMLInputElement;
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-ignore
        if (loqateInput) {
            loqateInput.value = '';
            ReactTestUtils.Simulate.change(loqateInput);
        }

        onChange &&
            onChange({
                name,
                value: EMPTY,
            });
    }, [html_id, name, onChange]);

    const renderInput = useCallback(
        (inputProps: any): JSX.Element => {
            return (
                <Input
                    readOnly={props.readOnly}
                    label={props.label}
                    type="text"
                    name={name}
                    placeholder={placeholder}
                    {...inputProps}
                    id={html_id}
                    required={props.required}
                    value={address?.label || inputProps.value}
                    suffix={props.readOnly ? <></> : <CloseCircleOutlined tw="absolute right-2" onClick={handleClear} />}
                />
            );
        },
        [address?.label, handleClear, html_id, name, placeholder, props.label, props.readOnly, props.required],
    );

    return (
        <Field name={`${name}-top`} errorMessage={errorMessage} helperText={<div>{props.helperText}</div>}>
            <div tw="relative">
                <Global
                    styles={css`
                        .react-loqate-default-list {
                            max-height: 350px;
                            overflow-y: auto;
                            z-index: 20;
                        }

                        .address-options {
                            ${THEME.BOX_SHADOW_GRAY};
                            li {
                                background-color: ${THEME.WHITE};
                                padding: 5px 10px !important;
                            }
                            li:hover {
                                background-color: ${THEME.GREEN};
                                color: ${THEME.WHITE};
                                cursor: pointer;
                            }
                        }
                    `}
                />
                <AddressSearch
                    className={props.className}
                    locale="en_GB"
                    countries={['GB']}
                    debounce={300}
                    apiKey={process.env.REACT_APP_LOQATE_API_KEY || ''}
                    onSelect={useCallback(
                        (address: any): void => {
                            consoleService.log('address', address);
                            const arg: IInputLoqateValue = {
                                city: address.City,
                                addressLine1: address.Line1,
                                addressLine2: [address.Line2, address.Line3, address.Line4, address.Line5]
                                    .filter((f) => f)
                                    .join('/'),
                                addressLine3: address.Line3,
                                country: address.CountryIso2,
                                postCode: address.PostalCode,
                                label: [
                                    address.Line1,
                                    [address.Line2, address.Line3, address.Line4, address.Line5].filter((f) => f).join('/'),
                                    address.City,
                                    address.PostalCode,
                                ]
                                    .filter((f) => f)
                                    .join(', '),
                                originalLabel: address.Label,
                            };
                            setAddress(arg);
                            onChange && onChange({ name, value: arg });
                        },
                        [name, onChange],
                    )}
                    components={useMemo(() => {
                        return {
                            Input: renderInput,
                        };
                    }, [renderInput])}
                    classes={useMemo(() => ({ list: 'address-options' }), [])}
                />
            </div>
        </Field>
    );
};

export const getInputLoqateLabel = (address: IInputLoqateValue): string => {
    const res = [
        address.addressLine1,
        [address.addressLine2, address.addressLine3].filter((f) => f).join('/'),
        address.city,
        address.postCode,
    ]
        .filter((f) => f)
        .join(', ');
    return res;
};
