import { CONST } from '@common/constants/const.constant';

export const formatPrice = (price: number): string => {
    if (!price) {
        return `${CONST.CURRENCY_SYMBOL}${price.toFixed(2)}`;
    }
    // return `£${price / 100}`;
    return `${CONST.CURRENCY_SYMBOL}${(price / 100).toFixed(2)}`;
};
