import { EPaymentMethod } from '@common/enums/payment-method.enum';
import { Field } from '@common/general-ui/field/field.component';
import { IRadioOption } from '@common/general-ui/radio-group/radio.component';
import { useField } from '@common/hooks/use-field.hook';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe, PaymentMethodResult, SetupIntentResult, Stripe, StripeCardNumberElement } from '@stripe/stripe-js';
import React, { useCallback, useEffect, useMemo } from 'react';
import { IStripeCard, StripeInternal } from './stripe-internal.component';
export type StripeEx = Stripe;
export type PaymentMethodResultEx = PaymentMethodResult;

export interface IStripeValue {
    card: StripeCardNumberElement | null | undefined;
    stripe_permanent_token_id: string | undefined;
    stripe_setup_indent_id: string | undefined;
    client_secret: string | undefined;
    paymentMethod: EPaymentMethod;
}
interface IProps {
    required?: boolean;
    name: string;
    onStripeInit?: (stripe: Stripe) => void;
    stripePublicKey: string;
    clientSecret?: string;
    setupIntentId?: string;
    onPaymentTabChange?: () => void;
    cardError?: string;
    paymentMethodOptions: IRadioOption[];
    defaultOptionName?: EPaymentMethod;
}

export const FormStripe = (props: IProps): JSX.Element | null => {
    const {
        name,
        required,
        paymentMethodOptions,
        onStripeInit,
        cardError,
        stripePublicKey,
        clientSecret,
        setupIntentId,
        onPaymentTabChange,
        defaultOptionName,
    } = props;

    const { setFieldTouched, setFieldValue, triggerValidate, errorMessage, value } = useField<IStripeValue>({
        ...props,
        required: true,
        getCustomErrorMessage: undefined,
    });
    const stripePromise = useMemo(() => {
        return stripePublicKey ? loadStripe(stripePublicKey) : undefined;
    }, [stripePublicKey]);

    const handleCardChange = useCallback(
        (e: IStripeCard) => {
            if (e.isValid) {
                setFieldValue({ ...value, card: e.cardNumberElement });
            } else {
                setFieldValue({ ...value, card: undefined });
            }

            triggerValidate();
        },
        [setFieldValue, triggerValidate, value],
    );

    const handleTokenInit = useCallback(
        (e: SetupIntentResult) => {
            setFieldValue({
                ...value,
                stripe_permanent_token_id: (e.setupIntent?.payment_method as string) || '',
                stripe_setup_indent_id: e?.setupIntent?.id || '',
            });
            setFieldTouched();
        },
        [setFieldTouched, setFieldValue, value],
    );

    useEffect(() => {
        if (clientSecret !== value.client_secret) {
            setFieldValue({ ...value, client_secret: clientSecret, stripe_setup_indent_id: setupIntentId });
        }
    }, [clientSecret, setupIntentId, setFieldValue, value]);

    const handleBlur = useCallback(() => {
        setFieldTouched();
    }, [setFieldTouched]);

    const handleTabChange = useCallback(
        (tab: EPaymentMethod) => {
            setFieldValue({ ...value, paymentMethod: tab });

            onPaymentTabChange && onPaymentTabChange();
        },
        [onPaymentTabChange, setFieldValue, value],
    );

    return stripePromise ? (
        <Field
            name={name}
            required={required}
            // label={' ' || t('Payment method (Credit / Debit Card)')}
            errorMessage={cardError || errorMessage}
        >
            <Elements
                stripe={stripePromise}
                options={{
                    fonts: [
                        {
                            cssSrc: `${document.location.origin}/stripeStyles.css`,
                        },
                        {
                            cssSrc: 'https://fonts.googleapis.com/css2?family=Overpass:wght@600&display=swap',
                        },
                    ],
                    appearance: {
                        variables: {},
                    },
                }}
            >
                <StripeInternal
                    defaultOptionName={defaultOptionName}
                    paymentMethodOptions={paymentMethodOptions}
                    onTabChange={handleTabChange}
                    paymentButtonClientSecret={props.clientSecret}
                    onPaymentButtonChange={handleTokenInit}
                    onStripeInit={onStripeInit}
                    onCardChange={handleCardChange}
                    onBlur={handleBlur}
                />
            </Elements>
        </Field>
    ) : null;
};
