import { IInputLoqateValue } from '@common/general-ui/input-loqate/input-loqate.component';
import { useField } from '@common/hooks/use-field.hook';
import React, { useCallback } from 'react';
import { Address, IAddressChangeHandler, IAddressProps } from './address.component';

export function FormAddress<TFormValues>(
    props: Omit<IAddressProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, getCustomErrorMessage } = props;
    const {
        setFieldValue,

        value,
        errorMessage,
        setFieldTouched: setFieldTouched,
    } = useField<IInputLoqateValue>({
        ...props,
        getCustomErrorMessage,
    });

    const handleChange = useCallback<IAddressChangeHandler>(
        (e) => {
            setFieldValue(e.value);
            onChange && onChange(e);
            setFieldTouched();
        },
        [onChange, setFieldTouched, setFieldValue],
    );
    return (
        <Address
            errorMessage={errorMessage}
            {...props}
            onChange={handleChange}
            name={props.name as string}
            defaultValue={value}
            value={value}
        />
    );
}
