import { IBEError } from '@common/interfaces/be-error.interface';
import { useCallback, useState } from 'react';
import { useTimeout } from './use-timeout.hook';

export interface IPromiseItem {
    id: string;
    promiseFn: () => Promise<unknown>;
}
export interface IPromiseResult {
    id: string;
    loading: boolean;
    success: boolean;
    error: IBEError | undefined;
}
interface IReturn {
    start: (promises: IPromiseItem[]) => void;
    loading: boolean;
    result: IPromiseResult[];
    clearResults: () => void;
}
export const useIndividualPromise = ({ parallel = true }: { parallel?: boolean }): IReturn => {
    const [loading, setIsLoading] = useState<boolean>(false);
    const [result, setResult] = useState<IPromiseResult[]>([]);
    const { start: startTimeout } = useTimeout<IPromiseItem[]>(
        useCallback(
            (promises: IPromiseItem[]): void => {
                if (!parallel) {
                    // eslint-disable-next-line @typescript-eslint/no-explicit-any
                    promises.reduce((acc, item?: IPromiseItem): any => {
                        return acc.finally(() => {
                            return item
                                ?.promiseFn()
                                .then((): void => {
                                    setResult((prevs): IPromiseResult[] => {
                                        return prevs.map((prev) => {
                                            if (prev.id === item.id) {
                                                return { id: item.id, loading: false, success: true, error: undefined };
                                            }
                                            return prev;
                                        });
                                    });
                                })
                                .catch((e) => {
                                    setResult((prevs): IPromiseResult[] => {
                                        return prevs.map((prev) => {
                                            if (prev.id === item.id) {
                                                return { id: item.id, loading: false, success: false, error: e };
                                            }
                                            return prev;
                                        });
                                    });
                                })
                                .finally(() => setIsLoading(false));
                        });
                    }, Promise.resolve());
                } else {
                    promises.forEach((item) => {
                        item.promiseFn()
                            .then((): void => {
                                setResult((prevs): IPromiseResult[] => {
                                    return prevs.map((prev) => {
                                        if (prev.id === item.id) {
                                            return { id: item.id, loading: false, success: true, error: undefined };
                                        }
                                        return prev;
                                    });
                                });
                            })
                            .catch((e) => {
                                setResult((prevs): IPromiseResult[] => {
                                    return prevs.map((prev) => {
                                        if (prev.id === item.id) {
                                            return { id: item.id, loading: false, success: false, error: e };
                                        }
                                        return prev;
                                    });
                                });
                            })
                            .finally(() => setIsLoading(false));
                    });
                }
            },
            [parallel],
        ),
        50,
    );

    return {
        start: useCallback(
            (promises: IPromiseItem[]): void => {
                setResult([]);
                setIsLoading(true);

                promises.forEach((item) => {
                    setResult((prev): IPromiseResult[] => {
                        return [...prev, { id: item.id, loading: true, success: false, error: undefined }];
                    });
                });

                startTimeout(promises);
            },
            [startTimeout],
        ),
        loading: result.some((r) => r.loading) || loading,
        clearResults: useCallback(() => {
            setResult([]);
        }, []),
        result,
    };
};
