import { THEME } from '@common/constants/theme.constant';
import { Field, IFieldProps } from '@common/general-ui/field/field.component';
import { Readonly } from '@common/general-ui/readonly.component';
import { useTimeout } from '@common/hooks/use-timeout.hook';
import { SerializedStyles } from '@emotion/react';
import { Input as AntdInput } from 'antd';
import React, { FocusEventHandler, InputHTMLAttributes, ReactNode, useCallback, useEffect, useState } from 'react';
import { css } from 'twin.macro';

export type IInputChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => void;

export interface IInputProps<T = string | number, TName = string> extends IFieldProps<T, TName> {
    prefix?: ReactNode;
    suffix?: ReactNode;
    minLength?: number;
    maxLength?: number;
    type: InputHTMLAttributes<T>['type'];
    className?: string;
    onChange?: IInputChangeHandler;
    onBlur?: (e: React.FocusEvent<HTMLInputElement>) => void;
    onFocus?: FocusEventHandler;
    onPaste?: (e: React.ClipboardEvent<HTMLInputElement>) => void;
    id?: string;
    autoComplete?: string;
    min?: number;
    max?: number;
    formatValue?: (val: string) => string;
}

export const Input = (props: IInputProps): JSX.Element => {
    const { size, errorMessage, helperText, validate: registerOptions, formatValue, ...rest } = props;
    // const inputRef = useRef<AntdInput>(); Have ref!!!
    const [val, setVal] = useState(props.defaultValue || props.value);
    const [isVisible, setIsVisible] = useState(props.autoComplete !== 'off');
    const { start } = useTimeout(() => {
        setIsVisible(true);
    }, 0);

    useEffect(() => {
        start(undefined);
    }, [start]);

    useEffect(() => {
        if (props.value !== undefined) {
            setVal(formatValue ? formatValue(props.value as string) : props.value);
        }
    }, [formatValue, props.value]);

    useEffect(() => {
        if (props.defaultValue !== undefined) {
            setVal(formatValue ? formatValue(props.defaultValue as string) : props.defaultValue);
        }
    }, [formatValue, props.defaultValue]);

    const handleChange = useCallback(
        (e: React.ChangeEvent<HTMLInputElement>) => {
            const newValue = e.target.value;

            setVal(formatValue ? formatValue(newValue) : newValue);
            props.onChange &&
                props.onChange({
                    ...e,
                    target: {
                        ...e.target,
                        value: formatValue ? (formatValue(newValue) as string) : newValue,
                    },
                });
        },
        [formatValue, props],
    );

    return (
        <Field {...props} tw="relative">
            <div tw="relative">
                {props.readOnly && <Readonly />}
                {/* https://stackoverflow.com/questions/15738259/disabling-chrome-autofill */}
                {isVisible && (
                    <AntdInput
                        {...rest}
                        value={val}
                        onChange={handleChange}
                        maxLength={props.maxLength}
                        css={inputStyles.input(!!props.readOnly)}
                        tabIndex={props.readOnly ? -1 : 0}
                    />
                )}
            </div>
        </Field>
    );
};

export const inputStyles = {
    input: (readOnly: boolean): (SerializedStyles | boolean)[] => [
        css`
            width: 100%;
            height: 34px;
            line-height: 34px;
            border: 1px solid ${THEME.GRAY};
            border-radius: 4px;
            font-weight: 400;
            font-size: ${THEME.BASE_FONT_SIZE};
            color: ${THEME.BLACK};
            padding: 6px 12px;
            box-sizing: border-box;

            && .ant-input {
                background-color: white;
                font-size: ${THEME.BASE_FONT_SIZE};
            }

            && .ant-input-prefix {
                color: ${THEME.GRAY};
                font-weight: 600;
                margin: 2px 0 0;
            }

            ::placeholder {
                color: ${THEME.GRAY};
            }

            :focus {
                outline-color: transparent;
                outline-style: none;
                ${THEME.BOX_SHADOW_GREEN}
            }

            :disabled,
            :disabled:hover {
                border-color: ${THEME.LIGHT_GRAY};
            }

            &&.ant-input-affix-wrapper:focus-within {
                outline-color: transparent;
                outline-style: none;
                ${THEME.BOX_SHADOW_GREEN}
            }
        `,
        readOnly &&
            css`
                border: 1px solid ${THEME.READONLY_COLOR};
                :hover {
                    border: 1px solid ${THEME.READONLY_COLOR};
                }
                :focus {
                    border: 1px solid ${THEME.READONLY_COLOR};
                    outline-color: transparent;
                    outline-style: none;
                    box-shadow: none;
                }
                .ant-input-affix-wrapper-readonly {
                    border: 1px solid ${THEME.READONLY_COLOR};
                }
                &&.ant-input-affix-wrapper:focus-within {
                    outline-color: transparent;
                    outline-style: none;
                    box-shadow: none;
                }
            `,
    ],
};
