import { formatUrl } from '@common/utils/format-url.util';
interface IProps {
    text: string;
    url: string;
    via?: string;
}
interface IReturn {
    email: (props: IProps & { subject: string }) => void;
    twitter: (props: IProps) => void;
    facebook: (props: IProps) => void;
    whatsApp: (props: IProps) => void;
    instagram: (props: IProps) => void;
}

export const useSocial = (): IReturn => {
    return {
        instagram: (): void => {
            // if (isAndroid) {
            //     const url = 'intent://instagram.com/#Intent;scheme=https;package=com.instagram.android;end';
            //     window.open(url);
            // } else if (isIOS) {
            //     window.location.replace('instagram://');
            //     setTimeout(() => {
            //         window.location.replace('https://apps.apple.com/us/app/instagram/id389801252');
            //     }, IOS_REDIRECT_TIMEOUT);
            // } else {
            //     window.open('https://instagram.com');
            // }
        },
        email: ({ text, subject }): void => {
            window.open(`mailto:?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(text)}`);
        },
        twitter: ({ text, url, via }: IProps): void => {
            window.open(formatUrl(`https://www.twitter.com/intent/tweet`, { text, url, via }));
            // if (isAndroid) {
            //     const url = 'intent://twitter.com/#Intent;scheme=https;package=com.twitter.android;end';

            //     window.open(url);
            // } else if (isIOS) {
            //     window.location.replace('twitter://');

            //     setTimeout(() => {
            //         window.location.replace('https://apps.apple.com/us/app/twitter/id333903271');
            //     }, IOS_REDIRECT_TIMEOUT);
            // } else {
            //     window.open('https://twitter.com');
            // }
        },
        facebook: ({ text, url, via }: IProps): void => {
            window.open(formatUrl(`https://www.facebook.com/share.php`, { u: url, text, via }));
            // if (isAndroid) {
            //     const url = 'intent://facebook.com/#Intent;scheme=https;package=com.facebook.katana;end';

            //     window.open(url);
            // }
            // else if (isIOS) {
            //     window.location.replace('facebook://');

            //     setTimeout(() => {
            //         window.location.replace('https://apps.apple.com/us/app/facebook/id284882215');
            //     }, IOS_REDIRECT_TIMEOUT);
            // } else {
            //     window.open('https://facebook.com');
            // }
        },
        whatsApp: ({ text }: IProps): void => {
            window.open(`https://wa.me/?text=${text}`);
            // if (isAndroid) {
            //     const url = 'intent://whatsapp.com/#Intent;scheme=https;package=com.whatsapp;end';

            //     window.open(url);
            // } else if (isIOS) {
            //     window.location.replace('whatsapp-messenger://');

            //     setTimeout(() => {
            //         window.location.replace('https://apps.apple.com/us/app/whatsapp-messenger/id310633997');
            //     }, IOS_REDIRECT_TIMEOUT);
            // } else {
            //     window.open('https://www.whatsapp.com/');
            // }
        },
    };
};
