import { EyeInvisibleOutlined, EyeOutlined } from '@ant-design/icons';
import { THEME } from '@common/constants/theme.constant';
import { Icon } from '@common/general-ui/icon/icon.component';
import { IInputProps, Input } from '@common/general-ui/input/input.component';
import React, { useCallback, useState } from 'react';
import { css } from 'twin.macro';

export interface IPasswordProps<T = string | number, TName = string> extends IInputProps<T, TName> {
    showPasswordIcon?: boolean;
}

export const Password = ({ showPasswordIcon = true, ...rest }: IPasswordProps): JSX.Element => {
    const { readOnly } = rest;
    const [showPassword, setShowPassword] = useState(false);

    const handleShowPasswordClick = useCallback(() => {
        setShowPassword(!showPassword);
    }, [showPassword]);

    const renderSuffix = (): JSX.Element => {
        return (
            <div onClick={handleShowPasswordClick}>
                {showPassword ? (
                    <Icon icon={EyeOutlined} css={styles.icon} />
                ) : (
                    <Icon icon={EyeInvisibleOutlined} css={styles.icon} />
                )}
            </div>
        );
    };

    return (
        <>
            {/* https://stackoverflow.com/questions/15738259/disabling-chrome-autofill */}
            {rest.autoComplete === 'off' ? <input type="password" name={rest.name} tw="hidden" /> : null}
            <Input
                autoComplete="off"
                {...rest}
                css={styles.root}
                suffix={!readOnly && showPasswordIcon ? renderSuffix() : undefined}
                type={showPassword ? 'text' : 'password'}
            />
        </>
    );
};

const styles = {
    root: css`
        && .ant-input {
            padding-top: 2px;
            vertical-align: inherit;
        }
        :focus {
            outline-color: transparent;
            outline-style: none;
            ${THEME.BOX_SHADOW_GREEN}
        }
    `,
    icon: css`
        vertical-align: inherit;
    `,
};
