export const includePath = (part: string, mainPath: string): boolean => {
    const mainPart1 = mainPath.split('/');
    const subPart2 = part.split('/');

    return mainPart1.every((p1, index) => {
        const p2 = subPart2[index];
        if (p2 === undefined) {
            return true;
        }
        if (p1.indexOf(':') >= 0 || p2.indexOf(':') >= 0) {
            return true;
        }
        return p1 === p2;
    });
};
