import { THEME } from '@common/constants/theme.constant';
import { Menu, MenuItemProps } from 'antd';
import React from 'react';
import { css } from 'twin.macro';

interface IProps extends MenuItemProps {
    className?: string;
}

export const MenuItem = (props: IProps): JSX.Element => {
    return <Menu.Item css={styles.root} {...props} />;
};

const styles = {
    root: css`
        &&.ant-menu-item {
            margin: 0;
            font-size: ${THEME.BASE_FONT_SIZE};
            background-color: white;
        }
        &&.ant-menu-item-selected {
            background-color: white;
            text-decoration: underline;
            font-weight: normal;
        }
        &&.ant-menu-item-selected::after {
        }
    `,
};
