import { Button } from '@common/general-ui/button/button.component';
import { DropDownPopup } from '@common/general-ui/drop-down/drop-down-popup.component';
import { DropDown } from '@common/general-ui/drop-down/drop-down.component';
import { t } from '@common/hooks/use-t.hook';
import React, { useCallback, useMemo } from 'react';
import 'twin.macro';

export interface IActionItem<T = string> {
    name: T;
    title: string;
    isVisible: boolean;
}

interface IProps<T> {
    actions: IActionItem<T>[];
    actionsCountInInlineView?: number;
    onActionClick?: (action: IActionItem<T>) => void;
}
export function ActionList<T>({ actionsCountInInlineView = 2, actions, onActionClick }: IProps<T>): JSX.Element {
    const mainActions = useMemo(() => {
        return actionsCountInInlineView + 1 < actions.length
            ? actions.slice(0, actionsCountInInlineView)
            : actions.slice(0, actionsCountInInlineView + 1);
    }, [actions, actionsCountInInlineView]);

    const subActions = useMemo(() => {
        return actions.length === actionsCountInInlineView + 1 ? [] : actions.slice(actionsCountInInlineView) || [];
    }, [actions, actionsCountInInlineView]);

    const createActionClickHandler = useCallback(
        (action: IActionItem<T>): (() => void) =>
            (): void => {
                onActionClick && onActionClick(action);
            },
        [onActionClick],
    );
    return (
        <div>
            <div tw="flex">
                {mainActions.map((action) => {
                    return (
                        <div key={action.name as unknown as string} tw="pl-1">
                            <Button
                                size="small"
                                variant="orange"
                                name={`aciton-${action.name}`}
                                onClick={createActionClickHandler(action)}
                            >
                                {action.title}
                            </Button>
                        </div>
                    );
                })}
                {subActions.length ? (
                    <div>
                        <DropDown
                            placement="bottomRight"
                            showArrowIcon={false}
                            popup={
                                <DropDownPopup>
                                    {subActions.map((subAction) => {
                                        return (
                                            <div key={subAction.name as unknown as string} tw="py-1">
                                                <Button
                                                    size="small"
                                                    variant="orange"
                                                    onClick={createActionClickHandler(subAction)}
                                                    name={`aciton-${subAction.name}`}
                                                >
                                                    {subAction.title}
                                                </Button>
                                            </div>
                                        );
                                    })}
                                </DropDownPopup>
                            }
                        >
                            <Button name="add" variant="orange" tw="ml-1" size="small">
                                {t('More options')} &nbsp;<span tw="rotate-90">&gt;</span>
                            </Button>
                        </DropDown>
                    </div>
                ) : undefined}
            </div>
        </div>
    );
}
