import { IStore, StoreContext } from '@common/store/store.context';
import { useContext, useMemo } from 'react';

export type TLayoutType = 'tabs' | 'groupBar';

interface IResult {
    store: IStore;
    updateStore: (data: IStore) => void;
}

export const useStore = (): IResult => {
    const context = useContext(StoreContext);

    return useMemo<IResult>((): IResult => {
        return {
            store: context.store,
            updateStore: context.updateStore,
        };
    }, [context]);
};
