import { THEME } from '@common/constants/theme.constant';
import { IIdObject } from '@common/interfaces/id-object.interface';
import FullCalendarOriginal, {
    DateSelectArg,
    EventApi,
    EventChangeArg,
    EventClickArg,
    EventContentArg,
    EventInput,
} from '@fullcalendar/react';
import TimeGridPlugin from '@fullcalendar/timegrid';
import React, { useCallback, useEffect, useState } from 'react';
import { css } from 'twin.macro';

{/* prettier-ignore */
}
const InteractionPlugin = require('@fullcalendar/interaction').default;
const DayGridPlugin = require('@fullcalendar/daygrid').default;
const ListPlugin = require('@fullcalendar/list').default;

export type DateSelectArgEx = DateSelectArg;
export type EventChangeArgEx = EventChangeArg;
export type EventContentArgEx = EventContentArg;

interface IFullCalendarState {
    weekendsVisible: boolean;
    currentEvents: EventApi[];
}

export interface ICalendarDateRange {
    start: Date;
    end: Date;
}
export const formatDateForCalendar = (date: Date): string => date.toISOString().replace(/T.*$/, ''); // YYYY-MM-DD of today
interface IProps<T> {
    className?: string;
    events: CustomEventInput[];
    onDateRangeChange?: (range: ICalendarDateRange) => void;
    onEventClick?: (event: CustomEventInput<T> & IIdObject, original: EventClickArg) => void;
    onEventChange?: (event: CustomEventInput<T> & IIdObject, original: EventChangeArg) => void;
    onDateSelect?: (select: DateSelectArg) => void;
    renderEventContent?: (eventContent: EventContentArg) => JSX.Element;
}

export interface CustomEventInput<T = unknown> extends IIdObject {
    title: string | undefined;
    start: Date | string;
    editable?: boolean;
    allDay?: boolean;
    end?: Date | string;
    metadata?: T;
    extraDate?: Date;
    durationEditable?: boolean;
    display?: EventInput['display'];
}
// TODO don't remove comments - most advanced component
export function FullCalendar<T>({
    events,
    className,
    onEventClick,
    renderEventContent,
    onEventChange,
    onDateRangeChange,
    onDateSelect,
}: IProps<T>): JSX.Element {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const calendarRef = React.createRef<{ getApi: () => { view: any } }>();
    const [state, setState] = useState<IFullCalendarState>({
        weekendsVisible: true,
        currentEvents: [],
    });

    const _handleWeekendsToggle = (): void => {
        setState({
            ...state,
            weekendsVisible: !state.weekendsVisible,
        });
    };

    const getCalendarApi = useCallback(() => calendarRef.current?.getApi(), [calendarRef]);

    useEffect(() => {
        if (getCalendarApi()) {
            const range = getCalendarApi()?.view.getCurrentData().dateProfile.renderRange;
            // consoleService.log('calendarApi', getCalendarApi());
            onDateRangeChange && onDateRangeChange(range);
        }
    }, [getCalendarApi, onDateRangeChange]);

    const handleDateSelect = (selectInfo: DateSelectArg): void => {
        onDateSelect && onDateSelect(selectInfo);
        // const title = prompt('Please enter a new title for your event');
        // const calendarApi = selectInfo.view.calendar;
        // calendarApi.unselect(); // clear date selection
        // if (title) {
        //     calendarApi.addEvent({
        //         id: createEventId(),
        //         title,
        //         start: selectInfo.startStr,
        //         end: selectInfo.endStr,
        //         allDay: selectInfo.allDay,
        //     });
        // }
    };

    const handleEventClick = (clickInfo: EventClickArg): void => {
        const id = clickInfo.event.id;
        const el = events.find((e) => e.id === id);
        onEventClick && onEventClick(el as CustomEventInput<T> & IIdObject, clickInfo);
    };

    const handleEventChange = (changeInfo: EventChangeArg): void => {
        const id = changeInfo.event.id;
        const el = events.find((e) => e.id === id);
        onEventChange && onEventChange(el as CustomEventInput<T> & IIdObject, changeInfo);
    };

    const handleEvents = (events: EventApi[]): void => {
        setState({
            ...state,
            currentEvents: events,
        });
    };

    return (
        <div css={styles.calendar} tw="font-primary" className={className}>
            <FullCalendarOriginal
                {...{ ref: calendarRef as unknown as React.LegacyRef<FullCalendarOriginal> }}
                plugins={[DayGridPlugin, TimeGridPlugin, InteractionPlugin, ListPlugin]}
                headerToolbar={{
                    left: 'prev,next today',
                    center: 'title',
                    right: 'dayGridMonth,dayGridWeek,listMonth',
                }}
                eventOverlap={(stillEvent): boolean => {
                    return stillEvent?.display !== 'background';
                }}
                initialView="dayGridMonth"
                editable={true}
                datesSet={onDateRangeChange}
                selectable={true} // uncomment
                selectMirror={true}
                // dayMaxEvents={true}
                weekends={state.weekendsVisible}
                // initialEvents={events} // alternatively, use the `events` setting to fetch from a feed
                events={events}
                selectOverlap={(stillEvent): boolean => {
                    return stillEvent?.display !== 'background';
                }}
                select={handleDateSelect}
                eventContent={renderEventContent || renderDefaultEventContent} // custom render function
                eventClick={handleEventClick}
                eventChange={handleEventChange}
                eventsSet={handleEvents} // called after events are initialized/added/changed/removed
                /* you can update a remote database when these fire:
							eventAdd={function(){}}
							eventChange={function(){}}
							eventRemove={function(){}}
							*/
            />
        </div>
    );
}

//eventContent:EventContentArg
function renderDefaultEventContent(eventContent: EventContentArg): JSX.Element {
    return (
        <div>
            <i>{eventContent.event.title}</i>
        </div>
    );
}

const styles = {
    calendar: css`
        && .fc-toolbar-title {
            font-family: 'Oddtype';
        }
        && .fc-button {
            background-color: ${THEME.GREEN};
            font-family: inherit;
            border: 0;
            outline: none;
            box-shadow: none;

            :hover,
            :active,
            :focus {
                background-color: ${THEME.GREEN};
                border: 0 !important;
                box-shadow: none !important;
            }
        }

        && .fc-button-active {
            background-color: ${THEME.GREEN};
            text-decoration: underline;
            z-index: 0;
        }

        && .fc-event {
            border-color: ${THEME.GREEN};
        }

        && .fc-event-main {
            // background-color: ${THEME.GREEN};
        }

        && .fc-bg-event {
            background-color: ${THEME.GRAY};
        }

        && .fc .fc-daygrid-bg-harness .fc-highlight {
            // background-color: ${THEME.GREEN};
        }
    `,
};
