import { Link } from '@common/general-ui/link/link.component';
import { t } from '@common/hooks/use-t.hook';
import { IFetchMoreRequestOptions } from '@common/interfaces/collection-request-options.interface';
import { ICollection } from '@common/interfaces/collection.interface';
import React, { useCallback } from 'react';
import 'twin.macro';

interface IProps {
    dataSource: ICollection<unknown>;
    className?: string;
    onFetchMore?: (pagination: IFetchMoreRequestOptions) => void;
}

export function Pagination({ dataSource: dataSource, onFetchMore }: IProps): JSX.Element {
    const handleCbPaginationChange = useCallback(() => {
        onFetchMore &&
            onFetchMore({
                next_page: dataSource.next_page,
            });
    }, [onFetchMore, dataSource]);

    if (!dataSource?.next_page) {
        return <></>;
    }
    return (
        <div tw="text-right p-2">
            <Link name="next" onClick={handleCbPaginationChange} tw="cursor-pointer">
                {t('Load more')}
            </Link>
        </div>
    );
}
