import { THEME } from '@common/constants/theme.constant';
import { SerializedStyles } from '@emotion/react';
import React, { ReactNode } from 'react';
import { css } from 'twin.macro';

interface IProps {
    avatarUrl?: string | undefined;
    header?: ReactNode;
    children?: ReactNode | JSX.Element[];
    className?: string;
    headerCss?: SerializedStyles;
    contentCss?: SerializedStyles;
    noHeader?: boolean;
}

export const Card = ({ header, children: children, className, headerCss, contentCss, noHeader }: IProps): JSX.Element => {
    return (
        <div css={styles.root} className={className}>
            {noHeader ? (
                <div css={styles.upperBorder}></div>
            ) : (
                header &&
                !noHeader && (
                    <div tw="flex" css={[styles.headerCnt, headerCss]}>
                        {/* {avatarUrl && <BGImage tw="w-12 h-12 m-1 " src={avatarUrl} />} */}
                        <div css={[styles.header()]}>{header}</div>
                    </div>
                )
            )}
            {children && <div css={[styles.content, contentCss]}>{children}</div>}
        </div>
    );
};

const styles = {
    root: css``,
    headerCnt: css`
        background-color: ${THEME.GREEN};
    `,
    upperBorder: css`
        border-top: 1px solid ${THEME.GRAY};
    `,
    header: (): (SerializedStyles | boolean)[] => [
        css`
            padding: 16px;
            font-size: ${THEME.BASE_FONT_SIZE};
            font-weight: 200;

            color: white;
            * {
                color: white !important;
            }
        `,
    ],
    content: css`
        border: 1px solid ${THEME.GRAY};
        border-top: 0;
    `,
};
