import { Link } from '@common/general-ui/link/link.component';
import { ILinkItem } from '@common/utils/make-links.util';
import { Breadcrumb as AntdBreadcrumb } from 'antd';
import React from 'react';

export interface IBreadCrumbItem {
    link: ILinkItem;
    title: string;
}

interface IProps {
    className?: string;
    breadcrumbs: IBreadCrumbItem[];
}

export const Breadcrumb = ({ breadcrumbs, className }: IProps): JSX.Element => {
    return (
        <AntdBreadcrumb className={className} data-testid="breadcrumbs">
            {breadcrumbs.map((item, index) => (
                <AntdBreadcrumb.Item key={item.link.toString()}>
                    {index === breadcrumbs.length - 1 ? (
                        <span tw="text-base">{item.title}</span>
                    ) : (
                        <Link href={item.link} name={item.link.toString()}>
                            {item.title}
                        </Link>
                    )}
                </AntdBreadcrumb.Item>
            ))}
        </AntdBreadcrumb>
    );
};
