import { THEME } from '@common/constants/theme.constant';
import { Button } from '@common/general-ui/button/button.component';
import { useScreenMediaQuery } from '@common/hooks/use-screen-media-query.hook';
import { t } from '@common/hooks/use-t.hook';
import React, { ReactNode, useCallback, useRef, useState } from 'react';
import ReactSlider from 'react-slick';
import 'slick-carousel/slick/slick-theme.css';
import 'slick-carousel/slick/slick.css';
import { css } from 'twin.macro';

interface IProps {
    dots: boolean;
    arrows: boolean;
    slidesToShow: number;
    slidesToScroll: number;
    slidesAmount: number;
    children: ReactNode;
    className?: string;
    onFinish?: () => void;
    finishButtonTitle?: string;
}

export const Slider = ({
    children,
    className,
    slidesToScroll,
    slidesToShow,
    slidesAmount,
    finishButtonTitle,
    onFinish,
}: IProps): JSX.Element => {
    const [focusedSlideIndex, setFocusedSlideIndex] = useState<number>(0);
    const sliderRef = useRef<ReactSlider | undefined>();
    const { isDownMD } = useScreenMediaQuery();
    const isLastSlideFocused = focusedSlideIndex === slidesAmount - 1;
    const finishButtonText = isLastSlideFocused && finishButtonTitle ? finishButtonTitle : t('Next');

    const handleBackClick = useCallback(() => {
        sliderRef.current?.slickPrev();
    }, []);

    const handleNextClick = useCallback(() => {
        if (isLastSlideFocused && onFinish) {
            onFinish();
        } else {
            sliderRef.current?.slickNext();
        }
    }, [onFinish, isLastSlideFocused]);

    const handleSlideChange = useCallback((_, next) => {
        setFocusedSlideIndex(next as number);
    }, []);

    return (
        <div css={styles.root} className={className}>
            <div tw="h-full relative">
                <div tw="w-full h-full">
                    <ReactSlider
                        ref={sliderRef as unknown as React.LegacyRef<ReactSlider>}
                        lazyLoad="progressive"
                        infinite={false}
                        dots={true}
                        arrows={false}
                        slidesToShow={slidesToShow}
                        slidesToScroll={slidesToScroll}
                        slidesPerRow={1}
                        adaptiveHeight={true}
                        swipe={isDownMD}
                        beforeChange={handleSlideChange}
                    >
                        {children as unknown as JSX.Element}
                    </ReactSlider>
                </div>
            </div>
            <div tw="grid grid-cols-2 grid-flow-col grid-column-gap[16px] p-4 md:p-6 absolute bottom-0 right-0 left-0">
                <Button
                    onClick={handleBackClick}
                    name="back"
                    variant="black-outline"
                    size="large"
                    tw="flex justify-center items-center text-base"
                    disabled={focusedSlideIndex === 0}
                    css={styles.button}
                >
                    <svg width="16" height="12" viewBox="0 0 12 16" xmlns="http://www.w3.org/2000/svg">
                        <path d="M2.01519 9.41524L0.600973 8.00103L7.99997 0.602036L9.41418 2.01625L2.01519 9.41524Z" />
                        <path d="M0.600973 8.00103L2.01519 6.58682L9.41418 13.9858L7.99997 15.4L0.600973 8.00103Z" />
                    </svg>
                    <span>{t('Back')}</span>
                </Button>
                <Button
                    onClick={handleNextClick}
                    name="next"
                    variant="orange-transparent"
                    size="large"
                    tw="flex justify-center items-center text-base"
                    css={styles.button}
                >
                    <span>{finishButtonText}</span>
                    {isLastSlideFocused ? (
                        ' '
                    ) : (
                        <svg width="16" height="12" viewBox="0 0 12 16" xmlns="http://www.w3.org/2000/svg">
                            <path d="M7.48481 6.58476L8.89903 7.99897L1.50003 15.398L0.085817 13.9838L7.48481 6.58476Z" />
                            <path d="M8.89903 7.99897L7.48481 9.41318L0.0858154 2.01419L1.50003 0.599976L8.89903 7.99897Z" />
                        </svg>
                    )}
                </Button>
            </div>
        </div>
    );
};

const styles = {
    root: css`
        width: 100%;
        max-width: 100%;

        .slick-dots {
            bottom: -36px;

            & li {
                width: 12px;
                height: 12px;
                margin: 0 8px;

                & button {
                    width: 12px;
                    height: 12px;
                    padding: 0;

                    &:hover {
                        &:before {
                            color: ${THEME.BLACK};
                            opacity: 1;
                            border: 0;
                            font-size: 14px;
                            width: 14px;
                            height: 14px;
                            line-height: 14px;
                        }
                    }

                    &:before {
                        width: 12px;
                        height: 12px;
                        line-height: 12px;
                        font-size: 12px;
                        opacity: 1;
                        border: 1px solid ${THEME.BLACK};
                        border-radius: 50%;
                        color: transparent;
                    }
                }
            }
        }

        .slick-dots li.slick-active button:before {
            opacity: 1;
            border: 0;
            font-size: 14px;
            width: 14px;
            height: 14px;
            line-height: 14px;
        }
    `,
    button: css`
        & svg {
            margin-bottom: 4px;

            & path {
                fill: ${THEME.BLACK};
                transition: all 0.3s ease-in-out;
            }
        }

        &:not([disabled])[name='back']:hover svg path,
        &:not([disabled])[name='back']:hover:focus svg path {
            fill: ${THEME.WHITE};
            transition: all 0.3s ease-in-out;
        }

        &[disabled][name='back'] {
            background: transparent;

            & svg path {
                fill: #d9d9d9;
            }
        }
    `,
};
