import { useField } from '@common/hooks/use-field.hook';
import React, { useCallback } from 'react';
import { IORSwitchProps, ORSwitch, ORSwitchChangeHandler } from './or-switch.component';

export function FormORSwitch<TFormValues>(
    props: Omit<IORSwitchProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, onBlur, getCustomErrorMessage } = props;
    const { setFieldValue, setFieldTouched, value, errorMessage, triggerValidate } = useField<boolean>({
        ...props,
        getCustomErrorMessage,
    });

    const handleChange = useCallback<ORSwitchChangeHandler>(
        (e) => {
            onChange && onChange(e);
            setFieldValue(e.target.checked);
            setFieldTouched();
            triggerValidate();
        },
        [onChange, setFieldValue, setFieldTouched, triggerValidate],
    );

    const handleBlur = useCallback(
        (e: React.FocusEvent<HTMLInputElement>) => {
            onBlur && onBlur(e);
            setFieldTouched();
        },
        [onBlur, setFieldTouched],
    );

    return (
        <ORSwitch
            errorMessage={errorMessage}
            {...props}
            onChange={handleChange}
            onBlur={handleBlur}
            name={props.name as string}
            value={value}
        />
    );
}
