// import FacebookIcon from '@common/assets/images/facebook-icon.svg';
// import GoogleIcon from '@common/assets/images/google-icon.svg';
import { MQ } from '@common/constants/mq.constant';
import { THEME } from '@common/constants/theme.constant';
import { EAuthentication } from '@common/enums/authentication.enum';
import { Button } from '@common/general-ui/button/button.component';
import { useCognito } from '@common/hooks/use-cognito.hook';
import { t } from '@common/hooks/use-t.hook';
import { ISerializedStylesList } from '@common/interfaces/serialized-styles-list.interface';
import React, { useCallback } from 'react';
import { css } from 'twin.macro';

interface IProps {
    authentication: EAuthentication;
    showFacebook?: boolean;
    showGoogle?: boolean;
    googleButtonTitle?: string;
    facebookButtonTitle?: string;
    className?: string;
    centeredButtons?: boolean;
}

export const SocialButtons = ({
    className,
    authentication,
    showFacebook,
    showGoogle,
    centeredButtons,
    googleButtonTitle,
    facebookButtonTitle,
}: IProps): JSX.Element => {
    const { loginWithFacebook, loginWithGoogle } = useCognito();
    const handleFacebook = useCallback(() => {
        loginWithFacebook(authentication);
    }, [authentication, loginWithFacebook]);

    const handleGoogle = useCallback(() => {
        loginWithGoogle(authentication);
    }, [authentication, loginWithGoogle]);

    return (
        <div className={className} css={styles.container(!!centeredButtons)}>
            {showGoogle && (
                <Button
                    variant="empty"
                    css={styles.google}
                    name="google-login"
                    type="button"
                    tw="block w-full mb-4 py-2"
                    onClick={handleGoogle}
                >
                    <div tw="flex justify-center">
                        {/* <img src={GoogleIcon} /> */}
                        <span tw="text-black pl-2 text-base ">
                            {googleButtonTitle ||
                                (authentication === EAuthentication.RegistrationSignIn
                                    ? t('Sign in via Google')
                                    : t('Login via Google'))}
                        </span>
                    </div>
                </Button>
            )}

            {showFacebook && (
                <Button
                    variant="empty"
                    css={styles.facebook}
                    name="facebook-login"
                    type="button"
                    tw="block w-full py-2"
                    onClick={handleFacebook}
                >
                    <div tw="flex justify-center">
                        {/* <img src={FacebookIcon} /> */}
                        <span tw="text-white pl-2 text-base ">
                            {facebookButtonTitle ||
                                (authentication === EAuthentication.RegistrationSignIn
                                    ? t('Sign in via Facebook')
                                    : t('Login via Facebook'))}
                        </span>
                    </div>
                </Button>
            )}
        </div>
    );
};

const button = css`
    padding-left: 6px;
    padding-right: 6px;
    width: auto;
    margin-right: 12px;
    height: 44px;
`;
const styles = {
    container: (centeredButtons: boolean): ISerializedStylesList => [
        centeredButtons &&
            css`
                ${MQ.md} {
                    margin: 0 auto;
                }
            `,
    ],
    facebook: css`
        ${button}
        background-color: rgb(59, 89, 152);
        :hover,
        :focus,
        :active {
            background-color: rgb(41, 62, 105);
        }
    `,
    google: css`
        ${button}
        border: 1px solid ${THEME.GRAY} !important;
        background: white;
        :hover,
        :focus,
        :active {
            background-color: rgb(239, 240, 238);
        }
    `,
};
