import { THEME } from '@common/constants/theme.constant';
import { Field, IFieldProps } from '@common/general-ui/field/field.component';
import { Readonly } from '@common/general-ui/readonly.component';
import { IDictionary } from '@common/interfaces/dictionary.interface';
import { css as emotionCSS } from '@emotion/css';
import { Global, SerializedStyles } from '@emotion/react';
import { TreeSelect as AntdTreeSelect } from 'antd';
import React, { ReactNode, useCallback, useEffect, useMemo, useState } from 'react';
import { css } from 'twin.macro';

const { TreeNode } = AntdTreeSelect;

export const TreeSelectOption = Option;
export type ITreeSelectChangeHandler = (e: { value: string; name: string }) => void;

export interface ITreeSelectProps<TName = string, TOption = unknown> extends IFieldProps<string, TName> {
    options?: ITreeSelectOption<TOption>[];
    onChange?: ITreeSelectChangeHandler;
    onBlur?: (e: React.FocusEvent<HTMLInputElement>) => void;
    className?: string;
    renderTreeSelectionOption?: (data: ITreeSelectOption<TOption>) => ReactNode;
    onSearch?: (data: string) => void;
    showSearch?: boolean;
    allowClear?: boolean;
    allowCustomValue?: boolean;
}

export interface ITreeSelectOption<T = unknown> {
    label: ReactNode;
    value: string | number;
    isVisible?: boolean;
    disabled?: boolean;
    data?: T;
    subOptions?: ITreeSelectOption<T>[];
}

export function TreeSelect<T = unknown>(props: ITreeSelectProps<string, T>): JSX.Element {
    const {
        onChange,
        onBlur,
        onSearch,
        allowCustomValue,
        options = [],
        defaultValue,
        value,
        name,
        showSearch = false,
    } = props;
    const [internalOptions, setInternalOptions] = useState<ITreeSelectOption<T>[]>(options || []);

    useEffect(() => {
        if (options !== undefined) {
            setInternalOptions(options);
        }
    }, [options]);

    const renderTreeNode = useCallback(
        (o: ITreeSelectOption<T>) => {
            return o.subOptions && o.subOptions.length ? (
                <TreeNode
                    data-parent-testid={'field-' + props.name}
                    key={o.value}
                    value={o.value}
                    disabled={o.disabled}
                    selectable={false}
                    title={o.label}
                >
                    {o.subOptions.map((option) => renderTreeNode(option))}
                </TreeNode>
            ) : (
                <TreeNode
                    data-parent-testid={'field-' + props.name}
                    key={o.value}
                    value={o.value}
                    disabled={o.disabled}
                    title={o.label}
                />
            );
        },
        [props.name],
    );

    const handleChange = useCallback(
        (value: string) => {
            onChange && onChange({ name, value });
        },
        [onChange, name],
    );

    const handleBlur = useCallback(
        (e: React.FocusEvent<HTMLInputElement>) => {
            onBlur && onBlur(e);
        },
        [onBlur],
    );

    const handleFocus = useCallback(() => {
        // consoleService.log('focus');
    }, []);

    const handleSearch = useCallback(
        (str: string) => {
            onSearch && onSearch(str);
            setInternalOptions([
                {
                    label: str,
                    value: str,
                    data: str as unknown as T,
                    isVisible: true,
                },
                ...(options || []),
            ]);
        },
        [onSearch, options],
    );

    const handleDropDownVisibilityChange = useCallback(
        (open: boolean) => {
            if (allowCustomValue && open) {
                setInternalOptions([...options]);
            }
        },
        [allowCustomValue, options],
    );

    const optionsCache = useMemo(() => {
        const res = (props?.options || []).reduce((acc: IDictionary, el: ITreeSelectOption) => {
            acc[el.value] = el.label;
            return acc;
        }, {});
        return res;
    }, [props.options]);

    const handleFilter = useCallback(
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        (input: string, option: any): boolean => {
            return optionsCache[option.value]?.toLowerCase().indexOf(input.toLowerCase()) >= 0 || option.value == input;
        },
        [optionsCache],
    );
    return (
        <Field {...props} tw="relative">
            <Global
                styles={css`
                    .ant-select-dropdown {
                        box-shadow: none;
                    }
                `}
            />
            <div tw="relative">
                {props.readOnly && <Readonly />}
                <AntdTreeSelect
                    // {...removeUndefined({ onSearch: props.readOnly && !showSearch ? undefined : handleSearch })}
                    css={styles.root(!!props.readOnly)}
                    allowClear={props.allowClear}
                    tabIndex={props.readOnly ? -1 : 0}
                    dropdownClassName={styles.dropDown}
                    showSearch={!props.readOnly && showSearch}
                    style={{ width: '100%' }}
                    placeholder={props.placeholder || ''}
                    onDropdownVisibleChange={handleDropDownVisibilityChange}
                    defaultValue={defaultValue}
                    value={value}
                    optionFilterProp="children"
                    onSearch={allowCustomValue ? handleSearch : undefined}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    dropdownRender={(prev): JSX.Element => (props.readOnly ? <></> : prev)}
                    filterTreeNode={handleFilter}
                    treeDefaultExpandAll
                >
                    {internalOptions.map((o: ITreeSelectOption<T>): JSX.Element => {
                        return props.renderTreeSelectionOption ? (
                            <React.Fragment key={o.value}>{props.renderTreeSelectionOption(o)}</React.Fragment>
                        ) : (
                            renderTreeNode(o)
                        );
                    })}
                </AntdTreeSelect>
            </div>
        </Field>
    );
}

const styles = {
    root: (readOnly: boolean): (SerializedStyles | boolean)[] => [
        css`
            display: block;
            && .ant-select-selector {
                height: 34px;
                border: 1px solid ${THEME.GRAY};
                border-radius: 4px;
                font-weight: 400;
                display: block;
                width: 100%;
                transition: border-color ease-in-out 0.15s, box-shadow ease-in-out 0.15s;
                overflow: hidden;
            }
            &.ant-select-focused {
                && .ant-select-selector {
                    border: 1px solid ${THEME.GREEN};
                    outline-color: transparent;
                    outline-style: none;
                    ${THEME.BOX_SHADOW_GREEN}
                }
            }
            && .ant-select-selection-placeholder {
                margin-top: 2px;
                display: block;
            }
            && .ant-select-selection-item {
                text-overflow: ellipsis;
                display: block;
                line-height: 34px;
            }
        `,
        readOnly &&
            css`
                && .ant-select-selector,
                && .ant-select-selector:hover {
                    border: 1px solid ${THEME.READONLY_COLOR};
                }
                &.ant-select-focused {
                    && .ant-select-selection-search-input,
                    && .ant-select-selection-item {
                        color: ${THEME.BLACK};
                    }
                    && .ant-select-selector {
                        border: 1px solid ${THEME.READONLY_COLOR};
                        outline-color: transparent;
                        outline-style: none;
                        box-shadow: none;
                    }
                }
            `,
    ],
    dropDown: emotionCSS`
			&& .ant-select-item-option-selected{
				background-color: #eee;
				color: black;
                font-weight: 400;
			}
			&& .ant-select-item-option-active {
            background-color: ${THEME.GREEN};
            color: white;
        }


    `,
};
