import { BGImage } from '@common/general-ui/image/image.component';
import { t } from '@common/hooks/use-t.hook';
import React from 'react';
import 'twin.macro';

type IProps = {
    src: string;
    className?: string;
};

export const ImagePreview = React.forwardRef<HTMLDivElement, IProps>(({ className, src }, _ref) => {
    return (
        <div className={className}>
            {src ? (
                <BGImage css={{ padding: '35%' }} tw="w-full h-full " src={src} />
            ) : (
                <div tw="w-full h-full flex justify-center align-middle border border-color[silver]">
                    <div tw="m-auto py-28">{t('No image for preview')}</div>
                </div>
            )}
        </div>
    );
});
