import { IDictionary } from '@common/interfaces/dictionary.interface';
import { equals } from './equals.util';

export function removeUndefined<T>(obj: T): T {
    return removeRecursive(obj, (arg: { name: string; value: unknown }): boolean => arg.value === undefined) || ({} as T);
}

export function removeUndefinedAndEmptyString<T>(obj: T): T {
    return (
        removeRecursive(
            obj,
            (arg: { name: string; value: unknown }): boolean => arg.value === undefined || arg.value === '',
        ) || ({} as T)
    );
}
export function removeDuplicate<T>(obj1: T, obj2?: T): T {
    return (
        removeRecursive(obj1, (arg: { name: string; value: unknown }): boolean => {
            return equals(arg.value, obj2 ? obj2[arg.name] : undefined);
        }) || ({} as T)
    );
}

export function removeDuplicateAndUndefined<T>(obj1: T, obj2?: T): T {
    const res1 = obj1 && obj2 ? removeDuplicate(obj1, obj2) : obj1;
    return removeUndefined(res1) || ({} as T);
}

function removeRecursive<T>(obj: T, canRemove: (arg: { name: string; value: unknown }) => boolean): T {
    if (obj) {
        if (Array.isArray(obj)) {
            return obj.map((v) => removeRecursive(v, canRemove)).filter((f) => f !== undefined) as unknown as T;
        }
        if (typeof obj === 'string' || typeof obj === 'number' || typeof obj === 'boolean' || obj instanceof Date) {
            return obj;
        }
        const keys = Object.keys(obj);
        const res = {} as IDictionary;
        keys.forEach((key) => {
            const val = obj[key];
            if (!canRemove({ name: key, value: val })) {
                if (Array.isArray(val)) {
                    const arrayValues = val.map((v) => removeRecursive(v, canRemove)).filter((f) => f !== undefined);
                    res[key] = arrayValues;
                } else if (typeof val === 'object' && val !== null && Object.keys(val).length > 0) {
                    const tmpVal = removeRecursive(val, canRemove);
                    if (!canRemove({ name: key, value: tmpVal })) {
                        res[key] = tmpVal;
                    }
                } else {
                    res[key] = val;
                }
            }
        });

        return Object.keys(res).length == 0 ? (undefined as unknown as T) : (res as unknown as T);
    }
    return undefined as unknown as T;
}
